CREATE OR REPLACE FUNCTION retirar_string ( ds_string_p text, qt_retirar_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_string_w		varchar(254);

-- se for informado um valor positivo (qt_retirar_p), retira a quantidade de caracteres referente ao valor informado do final da string.
-- se for informado um valor negativo (qt_retirar_p), retira a quantidade de caracteres referente ao valor informado do inÃ­cio da string.
BEGIN

if (qt_retirar_p = 0) then
	ds_string_w	:= ds_string_p;
elsif (qt_retirar_p > 0) then
	select	substr(ds_string_p, 1, length(ds_string_p) - qt_retirar_p)
	into STRICT	ds_string_w
	;

elsif (qt_retirar_p < 0) then
	select	substr(ds_string_p, abs(qt_retirar_p) + 1, length(ds_string_p))
	into STRICT	ds_string_w
	;
end if;

return	ds_string_w;

end;
$body$
