CREATE OR REPLACE FUNCTION retornar_atualizacoes_pep ( item_prontuario_p bigint, cd_profissional_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, nr_atendimento_p bigint, cd_pessoa_fisica_p text default null, item_pepo_p bigint default null, ie_item_oft_p bigint default null) RETURNS bigint AS $body$
DECLARE

qt_retorno_w	bigint;


BEGIN 
 
if (coalesce(nr_atendimento_p::text, '') = '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	 
	select	count(*) 
	into STRICT	qt_retorno_w 
	from	atualizacao_pep_usuario_v 
	where	cd_profissional = cd_profissional_p 
	and	dt_registro between dt_inicial_p and dt_final_p 
	and	((ie_item_prontuario = item_prontuario_p) or (ie_item_pepo = item_pepo_p) or (ie_item_oft = ie_item_oft_p)) 
	and	cd_pessoa_fisica = cd_pessoa_fisica_p;
 
elsif (coalesce(nr_atendimento_p::text, '') = '') then 
 
	select	count(*) 
	into STRICT	qt_retorno_w 
	from	atualizacao_pep_usuario_v 
	where	cd_profissional = cd_profissional_p 
	and	dt_registro between dt_inicial_p and dt_final_p 
	and	((ie_item_prontuario = item_prontuario_p) or (ie_item_pepo = item_pepo_p) or (ie_item_oft = ie_item_oft_p));
else 
	select	count(*) 
	into STRICT	qt_retorno_w 
	from	atualizacao_pep_usuario_v 
	where	cd_profissional = cd_profissional_p 
	and	dt_registro between dt_inicial_p and dt_final_p 
	and	((ie_item_prontuario = item_prontuario_p) or (ie_item_pepo = item_pepo_p) or (ie_item_oft = ie_item_oft_p)) 
	and	nr_atendimento = nr_atendimento_p;
 
end if;
 
return	qt_retorno_w;
 
end;
