CREATE OR REPLACE FUNCTION retornar_classif_diagnostico ( case_id_p bigint, cd_doenca_cid_p text, dt_diagnostico_p timestamp) RETURNS varchar AS $body$
DECLARE

				
s_classif_w           varchar(40);


BEGIN

	select 	case max(coalesce(IE_DIAG_TRATAMENTO, 'N')) 		when 'S' then '$BD;' else '' end ||
		case max(coalesce(ie_diag_princ_depart, 'N')) 	when 'S' then '$FHD;' else '' end ||
		case max(coalesce(ie_diag_admissao, 'N')) 		when 'S' then '$AD;' else '' end ||
		case max(coalesce(ie_diag_princ_episodio, 'N')) 	when 'S' then '$KHD;' else '' end ||
		case max(coalesce(ie_diag_alta, 'N')) 		when 'S' then '$ED;' else '' end ||
		case max(coalesce(ie_diag_cirurgia, 'N')) 		when 'S' then '$OD;' else '' end ||
		case max(coalesce(ie_diag_referencia, 'N')) 		when 'S' then '$UED;' else '' end
	into STRICT	s_classif_w
	from 		diagnostico_doenca a
	where 		a.nr_atendimento in (	SELECT nr_atendimento 
						from   atendimento_paciente 
						where  nr_seq_episodio = case_id_p)
	and 		a.cd_doenca like cd_doenca_cid_p
	and 		a.dt_diagnostico = dt_diagnostico_p;


return s_classif_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
