CREATE OR REPLACE FUNCTION retornar_prescr_atendimento ( nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_prescricao_ww	bigint;
nr_prescricao_w   bigint:= 0;

c01 CURSOR FOR
	SELECT	nr_prescricao
	from	   cirurgia
	where	   nr_atendimento = nr_atendimento_p
	and	   dt_inicio_real 	<= clock_timestamp()
	and	   dt_inicio_real    > clock_timestamp() - interval '2 days'
	and	   coalesce(dt_termino::text, '') = ''
	and	   ie_status_cirurgia not in (3,4)
	order by dt_inicio_real;


BEGIN
if (coalesce(nr_atendimento_p,0) > 0) then
	open C01;
	loop
	fetch C01 into
      	   nr_prescricao_ww;
	EXIT WHEN NOT FOUND; /* apply on C01 */
         		begin
         		nr_prescricao_w := nr_prescricao_ww;
         		end;
	end loop;
	close C01;
end if;

return nr_prescricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
