CREATE OR REPLACE FUNCTION retorna_criterio_atendimento_2 ( nr_atendimento_p nursing_care_encounter.nr_atendimento%TYPE, nr_seq_nivel_p nursing_care_encounter.nr_seq_nivel%TYPE ) RETURNS varchar AS $body$
DECLARE

    retorno_w    varchar(30);
    ie_existe_w  bigint;

BEGIN
    BEGIN
        SELECT
            CASE
                WHEN EXISTS (
                    SELECT
                        1
                    FROM
                        nursing_care_encounter x
                    WHERE
                            x.nr_atendimento = nr_atendimento_p
                        AND x.nr_seq_nivel = nr_seq_nivel_p
                        AND (x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
                ) THEN
                    1
                ELSE
                    0
            END
        INTO STRICT ie_existe_w
;

        IF ie_existe_w = 1 THEN
            retorno_w := obter_desc_expressao(928855, NULL);
        ELSE
            retorno_w := obter_desc_expressao(928857, NULL);
        END IF;

    EXCEPTION
        WHEN no_data_found THEN
            retorno_w := ' ';
    END;

    RETURN retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
