SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION retorna_evento_treinamento ( nr_seq_curso_p bigint, cd_pessoa_fisica_p text ) RETURNS varchar AS $body$
DECLARE


cd_treinamento_w	varchar(255);
ds_evento_w		varchar(255);
retorna_w		varchar(1000);


BEGIN

select	max(substr(obter_dados_treinamento(a.nr_seq_curso,'1'),1,200)) cd_treinamento,
	max(a.ds_evento)
into STRICT	cd_treinamento_w,
	ds_evento_w
from	tre_evento a,
	tre_inscrito b
where	a.nr_seq_curso = nr_seq_curso_p
and	a.nr_sequencia = b.nr_seq_evento
and	b.cd_pessoa_fisica = cd_pessoa_fisica_p;

retorna_w := cd_treinamento_w || ';' || ds_evento_w;

return	retorna_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
