CREATE OR REPLACE FUNCTION retorna_medico_externo (nr_atendimento_p bigint, campo_retorno_p bigint) RETURNS varchar AS $body$
DECLARE


  ds_medico_externo_w varchar(4000);


BEGIN
  if campo_retorno_p = 0 then
  select coalesce(max(nm_medico_externo), '')
    into STRICT ds_medico_externo_w
    from atendimento_paciente
   where nr_atendimento = nr_atendimento_p  LIMIT 1;
   else
  select coalesce(max(crm_medico_externo), '')
    into STRICT ds_medico_externo_w
    from atendimento_paciente
   where nr_atendimento = nr_atendimento_p  LIMIT 1;
   end if;

RETURN elimina_caractere_especial(ds_medico_externo_w);

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
