SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION retorna_milestone_ativ (nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


ie_milestone_w			varchar(1);
nr_seq_superior_w		bigint;
nr_seq_milestone_w		bigint;
nr_nivel_w			bigint;
nr_nivel_sup_w			bigint;


BEGIN

	select	a.nr_seq_superior,
		a.ie_milestone,
		proj_obter_nivel_etapa(a.nr_sequencia)
	into STRICT	nr_seq_superior_w,
		ie_milestone_w,
		nr_nivel_w
	from	proj_cron_etapa a
	where	a.nr_sequencia = nr_sequencia_p;

	if ie_milestone_w = 'S' then
		nr_seq_milestone_w := nr_sequencia_p;
	else
		select	proj_obter_nivel_etapa(b.nr_sequencia)
		into STRICT	nr_nivel_sup_w
		from	proj_cron_etapa b
		where	b.nr_sequencia = nr_seq_superior_w;

		if (nr_seq_superior_w IS NOT NULL AND nr_seq_superior_w::text <> '') and (nr_nivel_sup_w < nr_nivel_w) then
			nr_seq_milestone_w := retorna_milestone_ativ(nr_seq_superior_w);
		end if;
	end if;

return	nr_seq_milestone_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
