SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION retorno_contato_cih ( nr_ficha_ocorrencia_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 		varchar(1) := 'N';
nr_sequencia_w		bigint;
qt_dias_retorno_w	bigint;


BEGIN

qt_dias_retorno_w := obter_param_usuario(7043, 4, obter_perfil_ativo, nm_usuario_p, 0, qt_dias_retorno_w);

if (nr_ficha_ocorrencia_p IS NOT NULL AND nr_ficha_ocorrencia_p::text <> '') then

	if (qt_dias_retorno_w = 0 ) then
		ds_retorno_w := 'S';

	else


		select 	max(nr_sequencia)
		into STRICT	nr_sequencia_w
		from	cih_contato
		where   nr_ficha_ocorrencia = nr_ficha_ocorrencia_p;

		if (nr_sequencia_w > 0) then

			select 	coalesce(max(ie_complicacao_infect),'N')
			into STRICT	ds_retorno_w
			from 	cih_contato
			where	nr_sequencia = nr_sequencia_w
			and (clock_timestamp() - dt_contato ) >= qt_dias_retorno_w;


		end if;


	end if;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
