SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION retrieve_main_mrn (nr_prontuario_p bigint) RETURNS bigint AS $body$
DECLARE


nr_prontuario_w		pessoa_fisica.nr_prontuario%type;


BEGIN

select	max(nr_prontuario)
into STRICT	nr_prontuario_w
from	pessoa_fisica
where	nr_prontuario	= nr_prontuario_p;

if (coalesce(nr_prontuario_w::text, '') = '')	then
	select	max(pf.nr_prontuario)
	into STRICT	nr_prontuario_w
	from	pessoa_fisica pf,
		pessoa_fisica_duplic pfd
	where	pf.cd_pessoa_fisica	= pfd.cd_pessoa_fisica
	and	pfd.nr_prontuario	= nr_prontuario_p;
end if;

return nr_prontuario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
