CREATE OR REPLACE FUNCTION return_interv_plan_observ (nr_seq_interv_plan_p bigint) RETURNS text AS $body$
DECLARE


ds_retorno_w                        text;
separator            varchar(10) := '\r\n';
  linha RECORD;

BEGIN
    
    for linha in (
                    SELECT distinct cpg.nr_sequencia,
                                cpg.ds_guidance
                    from        cp_interv_plan_guid cpipg
                    inner join  cp_guidance cpg
                    on          cpipg.nr_seq_guidance = cpg.nr_sequencia
                    where       cpipg.nr_seq_interv_plan = nr_seq_interv_plan_p
    
    ) loop
        ds_retorno_w := ds_retorno_w || (linha.ds_guidance || separator);

    end loop;
return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
