SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION return_mat_integracao (nr_seq_interno_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


				
ds_material_int_w		varchar(200);
cd_material_int_w		varchar(20);
ds_retorno_w			varchar(255);


BEGIN

	select	max(b.cd_material_integracao),	
		max(b.ds_material_exame)
	into STRICT	cd_material_int_w,
		ds_material_int_w
	from	prescr_procedimento a,
		material_exame_lab b
	where	a.cd_material_exame = b.cd_material_exame
	and	a.nr_seq_interno     = nr_seq_interno_p;

  
	if (ie_opcao_p = 'C') then
		ds_retorno_w := cd_material_int_w;
	elsif (ie_opcao_p = 'D') then
		ds_retorno_w := ds_material_int_w;
	end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
