CREATE OR REPLACE FUNCTION return_notif_message (param1 text, param2 text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2000);


BEGIN
if (param2 = 'dt')then
  ds_retorno_w  :=   'DD-MON-YYYY HH24:MI:SS';
else
  ds_retorno_w	:= 	substr(replace_macro(expressao_pck.obter_desc_expressao(1053549), '@nm_pessoa_fisica', param1),1,2000);
  ds_retorno_w	:= 	substr(replace_macro(ds_retorno_w, '@dt_agendamento', param2),1,2000);
end if;			
RETURN	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
