CREATE OR REPLACE FUNCTION rop_obter_dados_classif ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(80);
ds_classificacao_w		varchar(80);

/* D - Descrição */

BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	ds_classificacao
	into STRICT	ds_classificacao_w
	from	rop_classificacao
	where	nr_sequencia	= nr_sequencia_p;

	if (ie_opcao_p = 'D') then
		ds_retorno_w	:= ds_classificacao_w;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
