SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION rop_obter_dados_lote_roupa (nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100);
cd_material_w		bigint;
ds_material_w		varchar(100);
ds_tamanho_w		varchar(80);
ds_classificacao_w	varchar(80);
ds_personalizacao_w	varchar(80);
ds_tipo_w		varchar(80);

/*
C  - Código
D  - Descrição
TA - Tamanho
CL - Classificacao
PE - Personalizacao
T  - Tipo
*/
BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	cd_material,
		substr(obter_desc_material(cd_material),1,100),
		substr(rop_obter_dados_tamanho(nr_seq_tamanho,'D'),1,80),
		substr(rop_obter_dados_classif(nr_seq_classificacao,'D'),1,80),
		substr(rop_obter_dados_person(nr_seq_personalizacao,'D'),1,80),
		substr(rop_obter_dados_tipo(nr_seq_tipo,'D'),1,80)
	into STRICT	cd_material_w,
		ds_material_w,
		ds_tamanho_w,
		ds_classificacao_w,
		ds_personalizacao_w,
		ds_tipo_w
	from	rop_lote_roupa
	where	nr_sequencia = nr_sequencia_p;

	if (ie_opcao_p	= 'C') then
		ds_retorno_w	:= cd_material_w;
	elsif (ie_opcao_p	= 'D') then
		ds_retorno_w	:= ds_material_w;
	elsif (ie_opcao_p	= 'TA') then
		ds_retorno_w	:= ds_tamanho_w;
	elsif (ie_opcao_p	= 'CL') then
		ds_retorno_w	:= ds_classificacao_w;
	elsif (ie_opcao_p	= 'PE') then
		ds_retorno_w	:= ds_personalizacao_w;
	elsif (ie_opcao_p	= 'T') then
		ds_retorno_w	:= ds_tipo_w;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
