CREATE OR REPLACE FUNCTION rop_obter_dados_roupa ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(80);
nr_seq_lote_roupa_w	bigint;
cd_barra_roupa_w	varchar(40);

/*
L - Sequência do Lote
C - Código de Barras
*/
BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	nr_seq_lote_roupa,
		cd_barra_roupa
	into STRICT	nr_seq_lote_roupa_w,
		cd_barra_roupa_w
	from	rop_roupa
	where	nr_sequencia = nr_sequencia_p;

	if (ie_opcao_p = 'L') then
		ds_retorno_w	:= nr_seq_lote_roupa_w;
	elsif (ie_opcao_p = 'C') then
		ds_retorno_w	:= cd_barra_roupa_w;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
