CREATE OR REPLACE FUNCTION rop_obter_dados_tecido (nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_tecido_w		varchar(80);
ds_fabricante_w		varchar(255);
qt_gramatura_w		double precision;

/*
T - Tecido
F - Fabricante
G - Gramatura
*/
BEGIN
select	ds_tecido,
	qt_gramatura,
	substr(obter_descricao_padrao('MAT_FABRICANTE','DS_FABRICANTE',nr_seq_fabricante),1,255)
into STRICT	ds_tecido_w,
	qt_gramatura_w,
	ds_fabricante_w
from	rop_tecido
where	nr_sequencia	= nr_sequencia_p;

if (ie_opcao_p = 'T') then
	ds_retorno_w	:= ds_tecido_w;
elsif (ie_opcao_p = 'G') then
	ds_retorno_w	:= qt_gramatura_w;
elsif (ie_opcao_p = 'F') then
	ds_retorno_w	:= ds_fabricante_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
