CREATE OR REPLACE FUNCTION rop_obter_dias_sem_movto (nr_seq_roupa_p bigint) RETURNS bigint AS $body$
DECLARE


dt_liberacao_w			timestamp;
dt_origem_w			timestamp;
qt_dias_w			bigint;


BEGIN

select	max(trunc(coalesce(a.dt_liberacao,clock_timestamp()),'dd'))
into STRICT	dt_liberacao_w
from	rop_lote_movto a,
	rop_movto_roupa b
where	a.nr_sequencia = b.nr_seq_lote
and	b.nr_seq_roupa = nr_seq_roupa_p
and	trunc(a.dt_liberacao,'dd') <> (
	SELECT	max(trunc(coalesce(a.dt_liberacao,clock_timestamp()),'dd'))
	from	rop_lote_movto a,
		rop_movto_roupa b
	where	a.nr_sequencia = b.nr_seq_lote
	and	b.nr_seq_roupa = nr_seq_roupa_p);

select	trunc(max(coalesce(dt_origem,clock_timestamp())),'dd')
into STRICT	dt_origem_w
from	rop_movto_roupa
where	nr_seq_roupa = nr_seq_roupa_p;

qt_dias_w := trunc(dt_origem_w - dt_liberacao_w);

return	qt_dias_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
