SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION rop_obter_dt_retorno_lav ( nr_sequencia_p bigint, cd_barras_p text) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w		timestamp;
dt_liberacao_w		timestamp;


BEGIN

select	dt_liberacao
into STRICT	dt_liberacao_w
from	rop_lote_movto
where	nr_sequencia = nr_sequencia_p;

if (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then
	select	min(a.dt_liberacao)
	into STRICT	dt_retorno_w
	from	rop_lote_movto a,
		rop_movto_roupa b
	where	a.nr_sequencia = b.nr_seq_lote
	and	a.dt_liberacao > dt_liberacao_w
	and	substr(rop_obter_dados_roupa(b.nr_seq_roupa, 'C'),1,40) = cd_barras_p
	and	rop_obter_tipo_local(a.nr_seq_local) = 'LI';
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
