CREATE OR REPLACE FUNCTION rop_obter_local_roupa ( cd_barra_roupa_p text) RETURNS bigint AS $body$
DECLARE


ds_retorno_w		bigint;
nr_seq_local_w		bigint;


BEGIN
if (cd_barra_roupa_p IS NOT NULL AND cd_barra_roupa_p::text <> '') then
	select	max(nr_seq_local)
	into STRICT	ds_retorno_w
	from	rop_roupa
	where	cd_barra_roupa = cd_barra_roupa_p
	and	ie_situacao = 'A';
end if;

return ds_retorno_w;

end;
$body$
