CREATE OR REPLACE FUNCTION rop_obter_preco_lav ( cd_estabelecimento_p bigint, cd_cnpj_p text) RETURNS bigint AS $body$
DECLARE


vl_preco_w		double precision;


BEGIN

select	coalesce(min(vl_preco),0)
into STRICT	vl_preco_w
from	rop_tabela_preco_lavacao
where	cd_estabelecimento = cd_estabelecimento_p
and	coalesce(cd_cnpj, cd_cnpj_p) = cd_cnpj_p
and	trunc(clock_timestamp(),'dd') between dt_inicio_vigencia and dt_fim_vigencia;

return	vl_preco_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
