CREATE OR REPLACE FUNCTION rop_obter_qt_lavadas ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w		bigint;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	count(*)
	into STRICT	qt_retorno_w
	from	rop_local c,
		rop_lote_movto b,
		rop_movto_roupa a
	where	a.nr_seq_lote = b.nr_sequencia
	and	b.nr_seq_local_orig_dest = c.nr_sequencia
	and	c.ie_tipo_local in ('LE','LI')
	and	a.nr_seq_roupa = nr_sequencia_p;
end if;

return qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
