CREATE OR REPLACE FUNCTION rop_obter_qt_min_max_roupa ( cd_estabelecimento_p bigint, cd_setor_atendimento_p bigint, nr_seq_roupa_p bigint, ie_opcao_p bigint) RETURNS bigint AS $body$
DECLARE

/*ie_opcao_p
0 - minimo
1 - maximo */
qt_minimo_w		double precision;
qt_maximo_w		double precision;
qt_retorno_w		double precision;


BEGIN

select	coalesce(max(qt_minimo),0),
	coalesce(max(qt_maximo),0)
into STRICT	qt_minimo_w,
	qt_maximo_w
from (
	SELECT	coalesce(max(qt_minimo),0) qt_minimo,
		coalesce(max(qt_maximo),0) qt_maximo
	from	rop_regra_roupa_setor
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	cd_setor_atendimento	= cd_setor_atendimento_p
	and	nr_seq_roupa		= nr_seq_roupa_p
	
union

	SELECT	coalesce(max(b.qt_minimo),0),
		coalesce(max(b.qt_maximo),0)
	from	rop_regra_roupa_setor a,
		rop_regra_roupa_setores b
	where	a.nr_sequencia		= b.nr_seq_regra
	and	a.cd_estabelecimento	= cd_estabelecimento_p
	and	a.cd_setor_atendimento	= b.cd_setor_atendimento
	and	b.cd_setor_atendimento	= cd_setor_atendimento_p
	and	a.nr_seq_roupa		= nr_seq_roupa_p) alias12;

if (ie_opcao_p = 0) then
	qt_retorno_w := qt_minimo_w;
elsif (ie_opcao_p = 1) then
	qt_retorno_w := qt_maximo_w;
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
