CREATE OR REPLACE FUNCTION rop_obter_saldo_roupa ( nr_seq_roupa_p bigint, nr_seq_local_p bigint, dt_mesano_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_saldo_w		double precision;


BEGIN

select	coalesce(sum(qt_saldo),0)
into STRICT	qt_saldo_w
from	rop_saldo_roupa
where	nr_seq_roupa = nr_seq_roupa_p
and	nr_seq_local = nr_seq_local_p
and	dt_mesano_referencia = dt_mesano_referencia_p;

return	qt_saldo_w;

end;
$body$
