CREATE OR REPLACE FUNCTION rop_obter_se_permite_local ( nr_seq_local_p bigint, nr_seq_local_lib_p bigint) RETURNS varchar AS $body$
DECLARE


ie_permite_w			varchar(1) := 'S';
qt_existe_w			bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	rop_local_lib
where	nr_seq_local	= nr_seq_local_p
and	nr_seq_local_lib	= nr_seq_local_lib_p
and	ie_liberar 		= 'N';

if (qt_existe_w > 0) then
	ie_permite_w := 'N';
end if;

return	ie_permite_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
