SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION rp_alerta_finalizacao (nr_seq_modelo_p text, nr_seq_pac_reab_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_pac_individual_w		bigint;
ie_alerta_w			varchar(1);
ie_retorno_w			varchar(1);


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_pac_individual_w
from	rp_pac_agend_individual
where	nr_seq_pac_reab	= nr_seq_pac_reab_p
and	coalesce(dt_fim_tratamento::text, '') = '';

select	coalesce(max(ie_verifica_mod_individual),'N')
into STRICT	ie_alerta_w
from	rp_modelo_agendamento
where	nr_sequencia	= nr_seq_modelo_p;

if (nr_seq_pac_individual_w IS NOT NULL AND nr_seq_pac_individual_w::text <> '') and (ie_alerta_w = 'S') then
	ie_retorno_w	:= 'S';
else
	ie_retorno_w	:= 'N';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
