SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION rp_obter_agenda_parametro (ie_tipo_agenda_p text) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w	bigint;
ds_retorno_w	varchar(255);
cd_estabelecimento_w bigint;
cd_perfil_w	bigint;


BEGIN

cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;
cd_perfil_w         	     := Obter_perfil_Ativo;

select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	rp_parametros
where	cd_estabelecimento = cd_estabelecimento_w
and	cd_perfil = coalesce(cd_perfil_w,cd_perfil);

if (nr_sequencia_w > 0) and (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
	select	CASE WHEN ie_tipo_agenda_p='GA' THEN cd_agenda_ga WHEN ie_tipo_agenda_p='GO' THEN cd_agenda_go END
	into STRICT	ds_retorno_w
	from	rp_parametros
	where	nr_sequencia = nr_sequencia_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
