CREATE OR REPLACE FUNCTION rp_obter_dados_agenda_consulta (nr_seq_agenda_p text, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(255);
			

BEGIN 
 
select	CASE WHEN ie_tipo_p='DAT' THEN to_char(dt_agenda,'dd/mm/yyyy hh24:mi:ss') WHEN ie_tipo_p='STA' THEN substr(obter_valor_dominio(83,ie_status_agenda),1,255) WHEN ie_tipo_p='DAG' THEN substr(obter_desc_agenda(cd_agenda),1,255)  END  
into STRICT	ds_retorno_w			 
from	agenda_consulta 
where	nr_sequencia = nr_seq_agenda_p;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
