CREATE OR REPLACE FUNCTION rp_obter_dados_lista_espera (cd_pessoa_fisica_p text, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(255);

BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (cd_pessoa_fisica_p > 0) then
	select 	max(CASE WHEN ie_tipo_p='DTI' THEN to_char(dt_inclusao_lista,'dd/mm/yyyy')  ELSE '' END )
	into STRICT	ds_retorno_w
	from	rp_lista_espera_modelo
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	ie_status = 'A';

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
