CREATE OR REPLACE FUNCTION rp_obter_desc_status (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN
ds_retorno_w := '';
if (nr_sequencia_p <> 0) and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select 	substr(ds_status,1,255)
	into STRICT	ds_retorno_w
	from	rp_status_reabilitacao
	where	nr_sequencia = nr_sequencia_p;
end if;



return	ds_retorno_w;

end;
$body$
