SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION rp_obter_disp_agenda (cd_agenda_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


qt_agendamento_w		bigint;
nr_sequencia_w			bigint;
qt_registros_agenda_w	bigint;
retorno_w				varchar(1);

BEGIN

--obter n√∫mero de agendas possiveis
select	max(a.qt_agendamento), max(a.nr_sequencia)
into STRICT	qt_agendamento_w,nr_sequencia_w
from	RP_TIPO_TRATAMENTO a,
		rp_tratamento_agenda b
where   a.nr_sequencia = b.nr_seq_tipo
and		b.cd_agenda = cd_agenda_p;

--obter a quantidade de registros daquela agenda
select count(distinct cd_agenda)
into STRICT	qt_registros_agenda_w
from   AGENDA_CONSULTA
where  1 = 1
and	   CD_AGENDA <> cd_agenda_p
and	   CD_PESSOA_FISICA = cd_pessoa_fisica_p;

if (coalesce(qt_agendamento_w,0) = 0) then
	retorno_w := 'S';
else
	if (qt_agendamento_w) > coalesce(qt_registros_agenda_w,0) then
		retorno_w := 'S';
	else
		retorno_w := 'N';
	end if;
end if;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION rp_obter_disp_agenda (cd_agenda_p bigint, cd_pessoa_fisica_p text) FROM PUBLIC;

