CREATE OR REPLACE FUNCTION rp_obter_lista_deficiencia_pf (cd_pessoa_fisica_p text, separador_p text default ',') RETURNS varchar AS $body$
DECLARE


ds_lista_w		varchar(3000);
ds_deficiencia_w	varchar(255);

C01 CURSOR FOR
	SELECT  substr(rp_obter_desc_deficiencia(nr_seq_tipo_def),1,255)
	from	pf_tipo_deficiencia
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	coalesce(dt_inativacao::text, '') = '';


BEGIN
ds_lista_w := '';
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	open c01;
	loop
	fetch c01 into ds_deficiencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		ds_lista_w := ds_lista_w || ds_deficiencia_w || separador_p;
		end;
	end loop;
	close c01;
end if;


return	substr(ds_lista_w,1,length(ds_lista_w) - 1);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
