CREATE OR REPLACE FUNCTION rp_obter_modelo_pac (nr_seq_pac_reab_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_modelo_agendamento_w	bigint;
nr_seq_pac_individual_w		bigint;
nr_seq_pac_modelo_w		bigint;
ds_modelo_w			varchar(80);
ds_retorno_w			varchar(80);


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_pac_modelo_w
from	rp_pac_modelo_agendamento
where	nr_seq_pac_reab = nr_seq_pac_reab_p
and	coalesce(dt_fim_tratamento::text, '') = ''
and	coalesce(dt_inativacao::text, '') = '';

select	max(nr_seq_modelo_agendamento)
into STRICT	nr_seq_modelo_agendamento_w
from	rp_pac_modelo_agendamento
where	nr_sequencia = nr_seq_pac_modelo_w
and	coalesce(dt_fim_tratamento::text, '') = ''
and	coalesce(dt_inativacao::text, '') = '';

select	substr(max(ds_modelo),1,80)
into STRICT	ds_modelo_w
from	rp_modelo_agendamento
where	nr_sequencia = nr_seq_modelo_agendamento_w;

select	max(nr_sequencia)
into STRICT	nr_seq_pac_individual_w
from	rp_pac_agend_individual
where	nr_seq_pac_reab = nr_seq_pac_reab_p
and	coalesce(dt_fim_tratamento::text, '') = '';

if (ds_modelo_w IS NOT NULL AND ds_modelo_w::text <> '') then
	ds_retorno_w	:= ds_modelo_w;
elsif (nr_seq_pac_individual_w IS NOT NULL AND nr_seq_pac_individual_w::text <> '') then
	ds_retorno_w	:= wheb_mensagem_pck.get_texto(799313);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
