CREATE OR REPLACE FUNCTION rp_obter_motivo_aprov_trat (nr_sequencia_p bigint, ds_motivo_p text default '') RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(255);


BEGIN

if (nr_sequencia_p <> 0) and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select 	substr(ds_motivo,1,255)
	into STRICT	ds_retorno_w
	from	rp_motivo_aprov_trat
	where	nr_sequencia = nr_sequencia_p;
else
	select 	max(nr_sequencia)
	into STRICT	ds_retorno_w
	from	rp_motivo_aprov_trat
	where	upper(ds_motivo) = upper(ds_motivo_p);
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
