CREATE OR REPLACE FUNCTION rp_obter_motivo_licenca (cd_pessoa_fisica_p text, dt_agenda_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') then

	SELECT 	MAX(SUBSTR(a.ds_motivo_licenca,1,255))
	INTO STRICT	ds_retorno_w
	FROM	rp_motivo_licenca a,
		rp_licenca b,
		rp_paciente_reabilitacao c
	WHERE	b.nr_seq_pac_reab = c.nr_sequencia
	AND	a.nr_sequencia = b.nr_seq_motivo_licenca
	AND	c.cd_pessoa_fisica = cd_pessoa_fisica_p
	AND	dt_agenda_p BETWEEN b.dt_periodo_inicial AND b.dt_periodo_final
	AND	b.NR_SEQUENCIA = (SELECT	MAX(b.nr_sequencia)
				  FROM		rp_motivo_licenca a,
						rp_licenca b,
						rp_paciente_reabilitacao c
				  WHERE		b.nr_seq_pac_reab = c.nr_sequencia
				  AND		a.nr_sequencia = b.nr_seq_motivo_licenca
				  AND		c.cd_pessoa_fisica = cd_pessoa_fisica_p
				  AND		(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
				  AND		dt_agenda_p BETWEEN b.dt_periodo_inicial AND b.dt_periodo_final);

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
