CREATE OR REPLACE FUNCTION rp_obter_qtd_vaga_restante_atx (nr_seq_modelo_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w	bigint;
qt_pac_modelo_w	bigint;
qt_vagas_w	bigint;
BEGIN

select	count(*)
into STRICT	qt_pac_modelo_w
from	rp_paciente_reabilitacao a,
	rp_pac_modelo_agendamento b
where	a.nr_sequencia = b.nr_seq_pac_reab
and	b.nr_seq_modelo_agendamento = nr_seq_modelo_p
and	coalesce(b.ie_situacao,'A') = 'A'
and	coalesce(dt_fim_tratamento::text, '') = '';

select	coalesce(qt_vagas,0)
into STRICT	qt_vagas_w
from	rp_modelo_agendamento
where	nr_sequencia = nr_seq_modelo_p;

qt_retorno_w	:= (qt_vagas_w - qt_pac_modelo_w);

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
