CREATE OR REPLACE FUNCTION rp_obter_seq_paciente_reab (cd_pessoa_fisica_p text, cd_estabelecimento_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);

BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (cd_pessoa_fisica_p > 0) then
	select 	max(a.nr_sequencia)
	into STRICT	ds_retorno_w
	from	rp_paciente_reabilitacao a, rp_tratamento b
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	a.nr_sequencia = b.nr_seq_pac_reav
	and	coalesce(b.dt_fim_tratamento::text, '') = ''
	and	a.cd_estabelecimento = cd_estabelecimento_p;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
