SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION rp_obter_sessoes_agendamentos (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w    	varchar(20);
qt_agendamentos_w	integer;
qt_items_w  		integer;



BEGIN
	SELECT 	COUNT(*)
	INTO STRICT    qt_agendamentos_w
	FROM	agenda_integrada_item  b
	WHERE   b.nr_seq_lista = nr_sequencia_p
	AND    	(b.nr_seq_agenda_cons IS NOT NULL AND b.nr_seq_agenda_cons::text <> '');

	SELECT 	MAX(coalesce(a.nr_sessoes,0))
	INTO STRICT    qt_items_w
	FROM	rp_lista_espera_modelo  a
	WHERE   a.nr_sequencia = nr_sequencia_p;

        ds_retorno_w := TO_CHAR(qt_agendamentos_w) || '/' || TO_CHAR(qt_items_w);

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
