CREATE OR REPLACE FUNCTION rp_obter_se_modelos_finalizado (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(10);
ie_existe_w	bigint;

BEGIN

--verifica se existe o paciente na tabela de modelos
select 	max(nr_sequencia)
into STRICT	ie_existe_w
from	rp_pac_modelo_agendamento
where	nr_seq_pac_reab = nr_sequencia_p;

if (ie_existe_w IS NOT NULL AND ie_existe_w::text <> '') then
	select 	count(*)
	into STRICT	ds_retorno_w
	from	rp_pac_modelo_agendamento
	where	coalesce(dt_fim_tratamento::text, '') = ''
	and	nr_seq_pac_reab = nr_sequencia_p;
else
	ds_retorno_w := '1';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
