SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION rp_obter_tipo_paciente ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
ds_retorno_ww	varchar(255);

c01 CURSOR FOR
	SELECT	d.ds_tipo_paciente
	from	rp_lista_espera_modelo a,
			rp_paciente_reabilitacao b,
			rp_tipo_pac_reab c,
			rp_tipo_paciente d
	where  	a.cd_pessoa_fisica = b.cd_pessoa_fisica
	and	   	b.nr_sequencia = c.nr_seq_pac_reab
	and	   	c.nr_tipo_pac_reab = d.nr_sequencia
	and	   	coalesce(c.dt_fim_tipo,clock_timestamp()) <= clock_timestamp()
	and	   	a.nr_sequencia = nr_sequencia_p;



BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	open c01;
	loop
	fetch c01 into
		ds_retorno_ww;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
			ds_retorno_w := substr(ds_retorno_w || ', ' || ds_retorno_ww,1,255);
			exit when length(ds_retorno_w) >= 254;
		elsif (coalesce(ds_retorno_w::text, '') = '') then
				ds_retorno_w := ds_retorno_ww;
		end if;
		end;
	end loop;
	close c01;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
