CREATE OR REPLACE FUNCTION rp_valida_data_ult_reuniao (nr_seq_pac_reab_p bigint) RETURNS varchar AS $body$
DECLARE


dt_ult_reuniao_w	varchar(255);
ds_retorno_w		varchar(10);

BEGIN

ds_retorno_w := 'S';
select 	rp_obter_data_ult_reuniao(nr_seq_pac_reab_p)
into STRICT	dt_ult_reuniao_w
;

if (dt_ult_reuniao_w IS NOT NULL AND dt_ult_reuniao_w::text <> '') then
