CREATE OR REPLACE FUNCTION rp_valida_dias_ult_aval (nr_seq_tratamento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2);
dt_ult_avaliacao	varchar(255);
qt_dias_w		bigint;


BEGIN
ds_retorno_w := 'S';
qt_dias_w := obter_param_usuario(9091, 2, obter_perfil_ativo, nm_usuario_p, 0, qt_dias_w);
qt_dias_w := 1;

select 	rp_dt_ultima_aval_alta(nr_seq_tratamento_p)
into STRICT	dt_ult_avaliacao
;

if (qt_dias_w > 0) and (dt_ult_avaliacao IS NOT NULL AND dt_ult_avaliacao::text <> '') then
	if (to_date(dt_ult_avaliacao,'dd/mm/yyyy hh24:mi:ss') < (clock_timestamp() - qt_dias_w)) then
		ds_retorno_w := 'N';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
