CREATE OR REPLACE FUNCTION rp_vigencia_lista_espera (cd_pessoa_fisica_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
dt_inclusao_w	varchar(255);
qt_dias_w	bigint;


BEGIN
qt_dias_w := obter_param_usuario(9091, 3, obter_perfil_ativo, nm_usuario_p, 0, qt_dias_w);
ds_retorno_w := 'S';
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (cd_pessoa_fisica_p > 0 ) then
	dt_inclusao_w := rp_obter_dados_lista_espera(cd_pessoa_fisica_p,'DTI');
	if (dt_inclusao_w IS NOT NULL AND dt_inclusao_w::text <> '') and (qt_dias_w > 0) then
		if (to_date(dt_inclusao_w,'dd/mm/yyyy') < (trunc(clock_timestamp()) - qt_dias_w)) then
				ds_retorno_w := 'N';
			end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
