CREATE OR REPLACE FUNCTION rxt_obter_agenda_simulacao (nr_seq_tipo_tratamento bigint) RETURNS bigint AS $body$
DECLARE

nr_sequencia_w	bigint;

BEGIN
SELECT	MIN(b.nr_sequencia)
into STRICT	nr_sequencia_w
FROM	rxt_equip_tipo a,
	rxt_equipamento b
WHERE	a.nr_seq_equipamento = b.nr_sequencia
AND	a.nr_seq_tipo = nr_seq_tipo_tratamento
AND	ie_tipo = 'S'
AND	ie_situacao = 'A';

return	nr_sequencia_w;

end;
