CREATE OR REPLACE FUNCTION rxt_obter_bloq_agenda_equip (cd_equipamento_p text, dt_inicial_p text, dt_final_p text, hr_inicial_p text, hr_final_p text) RETURNS bigint AS $body$
DECLARE


qt_agenda_w			bigint := 0;


BEGIN



if (hr_inicial_p <> '00:00:00') and (hr_final_p <> '00:00:00') then
	begin
	select	count(nr_sequencia)
	into STRICT	qt_agenda_w
	from	rxt_agenda
	where	1 = 1
	and             nr_seq_equipamento = cd_equipamento_p
	and             (nr_seq_tratamento IS NOT NULL AND nr_seq_tratamento::text <> '')
	and		trunc(dt_agenda) between dt_inicial_p and dt_final_p
	and		to_char(dt_agenda,'hh24:mi:ss') between hr_inicial_p and hr_final_p
	and		ie_status_agenda <> 'C';
	end;
elsif (hr_inicial_p <> '00:00:00') then
	begin
	select	count(nr_sequencia)
	into STRICT	qt_agenda_w
	from	rxt_agenda
	where	1 = 1
	and             nr_seq_equipamento = cd_equipamento_p
	and             (nr_seq_tratamento IS NOT NULL AND nr_seq_tratamento::text <> '')
	and		trunc(dt_agenda) between dt_inicial_p and dt_final_p
	and		to_char(dt_agenda,'hh24:mi:ss') > hr_inicial_p
	and		ie_status_agenda <> 'C';
	end;
elsif (hr_final_p <> '00:00:00') then
	begin
	select	count(nr_sequencia)
	into STRICT	qt_agenda_w
	from	rxt_agenda
	where	1 = 1
	and             nr_seq_equipamento = cd_equipamento_p
	and             (nr_seq_tratamento IS NOT NULL AND nr_seq_tratamento::text <> '')
	and		trunc(dt_agenda) between dt_inicial_p and dt_final_p
	and		to_char(dt_agenda,'hh24:mi:ss') < hr_final_p
	and		ie_status_agenda <> 'C';
	end;
else
	begin
	select	count(nr_sequencia)
	into STRICT	qt_agenda_w
	from	rxt_agenda
	where	1 = 1
	and             nr_seq_equipamento = cd_equipamento_p
	and             (nr_seq_tratamento IS NOT NULL AND nr_seq_tratamento::text <> '')
	and		trunc(dt_agenda) between dt_inicial_p and dt_final_p
	and		ie_status_agenda <> 'C';
	end;
end if;
return qt_agenda_w;
end;
