CREATE OR REPLACE FUNCTION rxt_obter_cor_planej (nr_seq_tratamento_p bigint, nr_seq_equipamento_p bigint, dt_inicio_tratamento_p timestamp, qt_duracao_tratamento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_status_planej_w	varchar(2000);
ie_status_agenda_w	varchar(15);
dt_tratamento_w	timestamp;
dt_termino_w	timestamp;


BEGIN
if (nr_seq_tratamento_p IS NOT NULL AND nr_seq_tratamento_p::text <> '') and (nr_seq_equipamento_p IS NOT NULL AND nr_seq_equipamento_p::text <> '') and (dt_inicio_tratamento_p IS NOT NULL AND dt_inicio_tratamento_p::text <> '') and (qt_duracao_tratamento_p IS NOT NULL AND qt_duracao_tratamento_p::text <> '') then
	begin

	dt_tratamento_w	:= to_date(to_char(trunc(dt_inicio_tratamento_p,'mm'),'dd/mm/yyyy') || ' ' ||

to_char(dt_inicio_tratamento_p,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss');

	select	max(dt_agenda)
	into STRICT	dt_termino_w
	from	rxt_agenda
	where	nr_seq_equipamento	= nr_seq_equipamento_p
	and	nr_seq_tratamento	= nr_seq_tratamento_p
	and	ie_tipo_agenda	= 'T';

	while(dt_tratamento_w <= dt_termino_w) loop
		begin
		select	coalesce(max(ie_status_agenda),'L')
		into STRICT	ie_status_agenda_w
		from	rxt_agenda
		where	nr_seq_equipamento	= nr_seq_equipamento_p
		and	nr_seq_tratamento	= nr_seq_tratamento_p
		and	trunc(dt_agenda)		= dt_tratamento_w
		and	ie_tipo_agenda	= 'T';

		dt_tratamento_w	:= dt_tratamento_w + 1;
		ie_status_planej_w	:= substr(ie_status_planej_w || ie_status_agenda_w || ';',1,2000);
		end;
	end loop;
	end;
end if;

return ie_status_planej_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
