SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION rxt_obter_dados_acessorio ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_tipo_acessorio_w	varchar(1);
ie_exige_classif_w		varchar(1);
ie_exige_medida_w		varchar(1);
ds_retorno_w		varchar(80);

/*
T - Tipo
C - Exige classificação
M - Exige medida
*/
BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	select	ie_tipo_acessorio,
		coalesce(ie_exige_classif,'N'),
		coalesce(ie_exige_medida,'N')
	into STRICT	ie_tipo_acessorio_w,
		ie_exige_classif_w,
		ie_exige_medida_w
	from	rxt_acessorio
	where	nr_sequencia = nr_sequencia_p;

	if (ie_opcao_p = 'T') then
		ds_retorno_w	:= ie_tipo_acessorio_w;
	elsif (ie_opcao_p = 'C') then
		ds_retorno_w	:= ie_exige_classif_w;
	elsif (ie_opcao_p = 'M') then
		ds_retorno_w	:= ie_exige_medida_w;
	end if;
	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
