CREATE OR REPLACE FUNCTION rxt_obter_dados_trat_agenda (nr_seq_tratamento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_trat_agenda_w		varchar(255);
nm_protocolo_w		varchar(40);
ds_tipo_w		varchar(40);
ds_topografia_w		varchar(100);
ds_convenio_w		varchar(255);

/*
P - Nome Protocolo
T - Tipo
C - Cido Topografia
V - ConvÃªnio
*/
BEGIN
if (nr_seq_tratamento_p IS NOT NULL AND nr_seq_tratamento_p::text <> '') then
	begin
	select	substr(rxt_obter_nome_protocolo(a.nr_seq_protocolo),1,40),
		substr(rxt_obter_desc_tipo(b.nr_seq_tipo),1,40),
		substr(obter_desc_cido_topografia(b.cd_cido),1,100),
		substr(Obter_Nome_Convenio(b.cd_convenio),1,100)
	into STRICT	nm_protocolo_w,
		ds_tipo_w,
		ds_topografia_w,
		ds_convenio_w
	from	rxt_tumor b,
		rxt_tratamento a
	where	b.nr_sequencia = a.nr_seq_tumor
	and	a.nr_sequencia = nr_seq_tratamento_p;

	if (ie_opcao_p = 'P') then
		ds_trat_agenda_w	:= nm_protocolo_w;
	elsif (ie_opcao_p = 'T') then
		ds_trat_agenda_w	:= ds_tipo_w;
	elsif (ie_opcao_p = 'C') then
		ds_trat_agenda_w	:= ds_topografia_w;
	elsif (ie_opcao_p = 'V') then
		ds_trat_agenda_w	:= ds_convenio_w;
	end if;
	end;
end if;

return ds_trat_agenda_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
