CREATE OR REPLACE FUNCTION rxt_obter_desc_aplicador_campo (nr_seq_braq_campo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_aplicador_w	varchar(255);


BEGIN

if (nr_seq_braq_campo_p IS NOT NULL AND nr_seq_braq_campo_p::text <> '') then

	select	max(a.ds_aplicador)
	into STRICT	ds_aplicador_w
	from	rxt_braq_aplicador a,
		rxt_braq_aplic_trat b,
		rxt_braq_campo_aplic_trat c
	where	a.nr_sequencia = b.nr_seq_aplicador
	and	b.nr_sequencia = c.nr_seq_aplic_trat
	and	c.nr_sequencia = nr_seq_braq_campo_p;
end if;

return	ds_aplicador_w;

end;
$body$
