CREATE OR REPLACE FUNCTION rxt_obter_desc_campo_trat (nr_seq_campo_trat_p bigint) RETURNS varchar AS $body$
DECLARE


ds_campo_trat_w         varchar(100);

BEGIN

if (nr_seq_campo_trat_p IS NOT NULL AND nr_seq_campo_trat_p::text <> '')  then

	select coalesce(to_char(rxt_obter_nome_campo_prot(nr_seq_campo)), ds_campo)
	into STRICT ds_campo_trat_w
	from rxt_campo
	where nr_sequencia = nr_seq_campo_trat_p;

end if;

return	ds_campo_trat_w;

end;
$body$
LANGUAGE PLPGSQL
